(41, '&lt;pre&gt;&lt;code class=&quot;language-php&quot;&gt;\n   if(getValue(&quot;cbbCategory&quot;,&quot;int&quot;,&quot;POST&quot;) == 0){\n      $fs_errorMsg .= &quot;&amp;amp;bull; Bạn chưa chọn loại b&agrave;i viết !&amp;lt;br/&amp;gt;&quot;;\n   }\n   $pos_search     =   removeAccent(getValue(&quot;pos_title&quot;, &quot;str&quot;, &quot;POST&quot;)).&quot; . &quot;.removeAccent(getValue(&quot;txt_content&quot;,&quot;str&quot;,&quot;POST&quot;));    \n   $myform-&amp;gt;add(&quot;pos_title&quot;, &quot;pos_title&quot;, 0, 0, 0, 1, &quot;Bạn chưa điền ti&ecirc;u đề !&quot;,1,&quot;Ti&ecirc;u đề b&agrave;i viết đ&atilde; tồn tại !&quot;);\n   $myform-&amp;gt;add(&quot;pos_time&quot;, $current_time, 1, 1, $current_time);\n   $myform-&amp;gt;add(&quot;pos_cat_id&quot;, &quot;cbbCategory&quot;, 1, 0, getValue(&quot;cbbCategory&quot;,&quot;int&quot;,&quot;POST&quot;), 1, &quot;Bạn chưa chọn loại b&agrave;i viết !&quot;);\n   //$myform-&amp;gt;add(&quot;pos_cat_tag&quot;, &quot;pos_cat_tag&quot;, 0, 0, &quot;&quot;, 1, &quot;Bạn chưa nhập tag cho b&agrave;i viết !&quot;);\n   $myform-&amp;gt;add(&quot;pos_content&quot;, &quot;txt_content&quot;, 6, 0, &quot;&quot;, 1, &quot;Bạn chưa nhập nội dung b&agrave;i viết !&quot;);\n   $myform-&amp;gt;add(&quot;pos_search&quot;, '''', 0, 0, removeHTML($pos_search) );\n   if(!$_FILES[''load_anh''][''name'']){\n      $fs_errorMsg .= &quot;&amp;lt;span style=''float:left;''&amp;gt;&amp;amp;bull; Bạn chưa upload ảnh minh họa cho b&agrave;i viết !&amp;lt;/span&amp;gt;&amp;lt;br/&amp;gt;&quot;;\n   }else{\n      $img_name = $upanh-&amp;gt;file_name;\n      $myform-&amp;gt;add(&quot;pos_img&quot;, $img_name, 0, 0, $img_name);\n   }\n   if(!$_FILES[''load_file''][''name'']){\n      $file_name = &quot;&quot;;\n   }else{\n      if($upfile-&amp;gt;common_error == &quot;&quot;){\n         $file_name = $upfile-&amp;gt;file_name;\n         $myform-&amp;gt;add(&quot;pos_att_file&quot;, $file_name, 0, 0, $file_name);\n      }else{\n         $fs_errorMsg .= $upfile-&amp;gt;common_error;\n      }\n         \n   }&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;', 1428639240, 89, 20017, '0', b'1', 0),
(42, '&lt;pre&gt;&lt;code class=&quot;language-php&quot;&gt;CREATE TABLE IF NOT EXISTS `admin_translate` (\n  `tra_keyword` varchar(255) CHARACTER SET utf8 NOT NULL,\n  `tra_text` varchar(255) CHARACTER SET utf8 DEFAULT NULL,\n  `lang_id` int(11) DEFAULT NULL,\n  `tra_source` varchar(255) CHARACTER SET utf8 DEFAULT NULL,\n  PRIMARY KEY (`tra_keyword`)\n) ENGINE=MyISAM DEFAULT CHARSET=latin1;&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;', 1429064640, 79, 20018, '0', b'1', 0),
(43, '&lt;pre&gt;&lt;code class=&quot;language-sql&quot;&gt;CREATE TABLE IF NOT EXISTS `admin_translate` (\n  `tra_keyword` varchar(255) CHARACTER SET utf8 NOT NULL,\n  `tra_text` varchar(255) CHARACTER SET utf8 DEFAULT NULL,\n  `lang_id` int(11) DEFAULT NULL,\n  `tra_source` varchar(255) CHARACTER SET utf8 DEFAULT NULL,\n  PRIMARY KEY (`tra_keyword`)\n) ENGINE=MyISAM DEFAULT CHARSET=latin1;&lt;/code&gt;&lt;/pre&gt;&lt;p&gt;&amp;nbsp;&lt;/p&gt;', 1429064700, 79, 20018, '0', b'1', 0);
